#!/bin/bash
echo "# user ssh"

keys=$(cat <<-EOL
# Alexandre Bini
ssh-rsa  alexandrebini

# Andreas
ssh-rsa  Andreas

# Lucas
ssh-rsa  lucas@agtlucas.com

# Klaus
ssh-rsa  klausy@gmail.com

## Circle CI deploy
# gb
ssh-rsa  circle@gb
# mttrs-api
ssh-rsa  circle@mttrs-api
# mttrs-web
ssh-rsa  circle@mttrs-web
# proxy
ssh-rsa  circle@proxy
# server
ssh-rsa  circle@server
EOL)

sudo mkdir -p /home/garageborn/.ssh
sudo sh -c "echo \"$keys\" > /home/garageborn/.ssh/authorized_keys"
sudo chown -Rf garageborn:garageborn /home/garageborn
sudo chmod 600 /home/garageborn/.ssh/authorized_keys
